pageContext.rootQuery:
{
    prismic {
        allHomepages {
            edges {
                node {
                    hero_title
                    hero_button_text
                    hero_button_link {
                        ... on PRISMIC__ExternalLink {
                            _linkType
                            url
                        }
                    }
                    content
                    about_title
                    about_bio
                    about_links {
                        about_link
                    }
                }
            }
        }
        allProjects {
            edges {
                node {
                    project_title
                    project_preview_description
                    project_preview_thumbnail
                    project_category
                    project_post_date
                    secondary_color
                    primary_color
                    _meta {
                        uid
                    }
                }
            }
        }
    }
    site {
        siteMetadata {
            title
            description
            author
        }
    }
}
