pageContext.rootQuery:
{
    prismic {
        allProjects {
            edges {
                node {
                    project_title
                    project_preview_description
                    project_preview_thumbnail
                    project_category
                    project_post_date
                    secondary_color
                    primary_color
                    _meta {
                        uid
                    }
                }
            }
        }
    }
    site {
        siteMetadata {
            title
            description
            author
        }
    }
}
