pageContext.rootQuery:
{
    prismic {
        allPosts(sortBy: post_date_DESC) {
            edges {
                node {
                    post_title
                    post_date
                    post_category
                    project_link {
                        ... on PRISMIC__ExternalLink {
                            url
                        }
                    }
                    post_preview_description
                    post_author
                    _meta {
                        uid
                    }
                }
            }
        }
    }
    site {
        siteMetadata {
            title
            description
            author
        }
    }
}
