pageContext.rootQuery:
{
    prismic {
        allPersons {
          edges {
            node {
              name
              title
              link {
                ... on PRISMIC__ExternalLink {
                  url
                }
              }
              image
              bio
            }
          }
        }
      }
    site {
        siteMetadata {
            title
            description
            author
        }
    }
}
